import React, { useState } from "react";

export default function Textarea(props) {
  const handleUpClick = () => {
    // console.log("handleUpClick was clicked");
    let newText;
    newText=text.toUpperCase();
    setText(newText);
    props.showAlert('Converted to upper case','success');
  };
  const handleLoClick = () => {
    // console.log("handleUpClick was clicked");
    let newText;
    newText=text.toLowerCase();
    setText(newText);
    props.showAlert('Converted to lower case','success');
  };
  const handleClearClick = () => {
    // console.log("handleUpClick was clicked");

    let vartext;
    vartext=document.getElementById('mybox');
    vartext.select();
    navigator.clipboard.writeText(vartext.value);


  };
  const handleOnChange = (e) => {
    setText(e.target.value);
  };
  const [text, setText] = useState("Enter your text here");
  return (
    <>
      <div className="container" style={{color: props.mode==='dark'?'white':'black'}}>
        <h1>{props.heading}</h1>
        <div className="input-group">
          {/* <span className="input-group-text">With textarea</span> */}
          <textarea style={{backgroundColor: props.mode==='dark'?'grey':'white'}}
            className="form-control new"
            id="mybox"
            value={text}
            onChange={handleOnChange}
            aria-label="With textarea"
          ></textarea>
        </div>
        <button className="btn btn-primary mx-1 my-2" onClick={handleUpClick}>
          Convert to Upper Case
        </button>
        <button className="btn btn-primary mx-1 my-2" onClick={handleLoClick}>
          Convert to Lower Case
        </button>
        <button className="btn btn-primary mx-1 my-2" onClick={handleClearClick}>
          Copy Text
        </button>
      </div>
      <div className="container my-3" style={{color: props.mode==='dark'?'white':'black'}}>
          <h1>Your Text Summary</h1>
          <p>{text.split(" ").length} words {text.length} characters</p>
          <p>On an average it will take {0.008*text.split(" ").length} Minutes to read</p>
          <h2>Preview</h2>
          <p>{text}</p>
      </div>
    </>
  );
}
