import React from "react";

export default function Textarea(props) {
  return (
    <>
      <h1>{props.heading}</h1>
      <div className="input-group">
          <div>
        <textarea
          className="form-control new" 
          aria-label="With textarea"
        ></textarea>
        </div>
      </div>
    </>
  );
}
