import React from "react";

export default function Alert(props) {
  return (
   props.alert && <div
      className={`alert alert-${props.type} alert-dismissible fade show`}
      role="alert"
    >
      <strong>{props.alert.msg}</strong>: {props.alert.type}
      <button
        type="button"
        className="btn-close"
        data-bs-dismiss="alert"
        aria-label="Close"
      ></button>
    </div>
  );
}
