import { useState } from 'react';
import './App.css';
import Alert from './Components/Alert';
// import About from './Components/About';
import Navbar from './Components/Navbar';
import Textarea from './Components/Textarea';

function App() {
  const[mode,setMode]=useState('light');
  const[alert,setAlert]=useState(null);
  const showAlert=(message,type)=>{
    msg: message,
    type=type

  }
  const toggleMode=()=>{
    if(mode==='light')
    {
      setMode('dark');
      document.body.style.backgroundColor='#62638a';
      showAlert('Dark mode has been enbled','success');

    }
    else{
      setMode('light');
      document.body.style.backgroundColor='white';
      showAlert('Light mode has been enbled','success');
    }
  }

  return (
   <>
   <Navbar title="Text Utils" aboutText="About TextUtils" mode={mode} toggleMode={toggleMode}/>
   <Alert alert={alert}/>
   <div className="container my-3">
     {/* <About/> */}
   <Textarea heading="Enter the Text to analyze" mode={mode}/>
   </div>
   </>
  );
}
export default App;
